vite.config.ts
&#x2F;&#x2F; vite.config.ts
import &#123; defineConfig &#125; from &quot;vite&quot;;
import react from &quot;@vitejs&#x2F;plugin-react&quot;;
&#x2F;&#x2F;@ts-ignore
import path from &quot;path&quot;;
import vitePluginImp from &quot;vite-plugin-imp&quot;;
import externalGlobals from &quot;rollup-plugin-external-globals&quot;;
import viteCompression from &quot;vite-plugin-compression&quot;;
import &#123; terser &#125; from &quot;rollup-plugin-terser&quot;;
import &#123; visualizer &#125; from &quot;rollup-plugin-visualizer&quot;;
const __dirname &#x3D; path.resolve();
&#x2F;&#x2F; 一些需要单独打成bundle的文件
const chunk1 &#x3D; [&quot;react&quot;, &quot;react-router-dom&quot;, &quot;react-dom&quot;];
const chunk2 &#x3D; [&quot;moment&quot;, &quot;@ant-design&#x2F;icons&quot;, &quot;ahooks&quot;];
const chunk3 &#x3D; [&quot;lodash&quot;];
const chunk4 &#x3D; [&quot;axios&quot;];
const antdChunk &#x3D; [&quot;antd&quot;];
const plotsChunk &#x3D; [&quot;@ant-design&#x2F;plots&quot;];

const build &#x3D; &#123;
  rollupOptions: &#123;
    output: &#123;
      &#x2F;&#x2F; 文件指纹
      chunkFileNames: &quot;static&#x2F;js&#x2F;[name].[hash].js&quot;,
      entryFileNames: &quot;static&#x2F;js&#x2F;[name].[hash].js&quot;,
      &#x2F;&#x2F; 分类输出
      assetFileNames: &quot;static&#x2F;[ext]&#x2F;[name].[hash].[ext]&quot;,
      &#x2F;&#x2F; 手动chunk
      manualChunks: &#123;
        vendor: chunk1,
        vendor2: chunk2,
        vendor3: chunk3,
        vendor4: chunk4,
        plots: plotsChunk,
        antd: antdChunk,
      &#125;,
    &#125;,
  &#125;,
  chunkSizeWarningLimit: 1024,
&#125;;

const server &#x3D; &#123;
  port: 3006,
  open: &quot;&#x2F;index.html&quot;,
  proxy: &#123;
    &quot;&#x2F;api&quot;: &#123;
      target: &quot;http:&#x2F;&#x2F;localhost:3032&quot;,
      changeOrigin: true,
      &#x2F;&#x2F; rewrite: (path) &#x3D;&gt; path.replace(&#x2F;^\&#x2F;api&#x2F;, &#39;&#39;)
    &#125;,
  &#125;,
&#125;;

const plugins &#x3D; [
  react(),
  vitePluginImp(&#123;
    libList: [
      &#x2F;&#x2F; antd 按需引入
      &#123;
        libName: &quot;antd&quot;,
        style: (name) &#x3D;&gt; &#96;antd&#x2F;es&#x2F;$&#123;name&#125;&#x2F;style&#96;,
      &#125;,
    ],
  &#125;),
  externalGlobals(&#123;
    &#x2F;&#x2F; &quot;@ant-design&#x2F;charts&quot;: &quot;window.charts&quot;
  &#125;),
  &#x2F;&#x2F; vite打包优化 - 压缩
  viteCompression(&#123;
    verbose: true,
    disable: false,
    threshold: 10240,
    algorithm: &quot;gzip&quot;,
    ext: &quot;.gz&quot;,
  &#125;),
  &#x2F;&#x2F; vite 打包优化 - 移除 日志&#x2F;debugger
  terser(&#123;
    compress: &#123;
      defaults: false,
      &#x2F;&#x2F; drop_console: true,
    &#125;,
    mangle: &#123;
      eval: true,
      module: true,
      toplevel: true,
      safari10: true,
      properties: false,
    &#125;,
    &#x2F;&#x2F; 不输出注释
    output: &#123;
      comments: false,
    &#125;,
  &#125;),
  &#x2F;&#x2F; vite 打包分析
  visualizer(),
];

&#x2F;&#x2F; https:&#x2F;&#x2F;vitejs.dev&#x2F;config&#x2F;
export default defineConfig(&#123;
  plugins,
  css: &#123;
    &#x2F;&#x2F; 配置antd主题
    preprocessorOptions: &#123;
      less: &#123;
        javascriptEnabled: true,
        modifyVars: &#123;
          &#x2F;&#x2F; 在这里自定义主题色等样式
          &quot;@primary-color&quot;: &quot;#646cff&quot;, &#x2F;&#x2F;设置antd主题色
        &#125;,
      &#125;,
    &#125;,
    &#x2F;&#x2F; 模块化样式，启用小驼峰
    modules: &#123;
      localsConvention: &quot;camelCase&quot;,
    &#125;,
  &#125;,
  &#x2F;&#x2F; 路径别名，简化长路径引入，看着不舒服
  resolve: &#123;
    alias: &#123;
      &quot;~&quot;: path.resolve(__dirname, &quot;.&#x2F;&quot;), &#x2F;&#x2F; 根路径
      &quot;@&quot;: path.resolve(__dirname, &quot;src&quot;), &#x2F;&#x2F; src 路径
    &#125;,
  &#125;,
  server,
  build,
&#125;);




