管理状态历史变化记录，方便在历史记录中前进与后退。
import &#123; useRef, useState &#125; from &quot;react&quot;;
import useMemoizedFn from &quot;..&#x2F;useMemoizedFn&quot;;
import &#123; isNumber &#125; from &quot;..&#x2F;utils&quot;;

interface IData&lt;T&gt; &#123;
  present?: T;
  past: T[];
  future: T[];
&#125;

const dumpIndex &#x3D; &lt;T&gt;(step: number, arr: T[]) &#x3D;&gt; &#123;
  let index &#x3D;
    step &gt; 0
      ? step - 1 &#x2F;&#x2F; move forward
      : arr.length + step; &#x2F;&#x2F; move backward
  if (index &gt;&#x3D; arr.length - 1) &#123;
    index &#x3D; arr.length - 1;
  &#125;
  if (index &lt; 0) &#123;
    index &#x3D; 0;
  &#125;
  return index;
&#125;;

const split &#x3D; &lt;T&gt;(step: number, targetArr: T[]) &#x3D;&gt; &#123;
  const index &#x3D; dumpIndex(step, targetArr);
  return &#123;
    _current: targetArr[index],
    _before: targetArr.slice(0, index),
    _after: targetArr.slice(index + 1),
  &#125;;
&#125;;

export default function useHistoryTravel&lt;T&gt;(initialValue?: T) &#123;
  const [history, setHistory] &#x3D; useState&lt;IData&lt;T | undefined&gt;&gt;(&#123;
    present: initialValue, &#x2F;&#x2F; 当前
    past: [], &#x2F;&#x2F; 历史记录
    future: [], &#x2F;&#x2F; 未来记录
  &#125;);

  const &#123; present, past, future &#125; &#x3D; history;

  const initialValueRef &#x3D; useRef(initialValue);

  const reset &#x3D; (...params: any[]) &#x3D;&gt; &#123;
    const _initial &#x3D; params.length &gt; 0 ? params[0] : initialValueRef.current;
    initialValueRef.current &#x3D; _initial;

    setHistory(&#123;
      present: _initial,
      future: [],
      past: [],
    &#125;);
  &#125;;

  const updateValue &#x3D; (val: T) &#x3D;&gt; &#123;
    setHistory(&#123;
      present: val,
      future: [],
      past: [...past, present],
    &#125;);
  &#125;;
  &#x2F;&#x2F; 向前
  const _forward &#x3D; (step: number &#x3D; 1) &#x3D;&gt; &#123;
    if (future.length &#x3D;&#x3D;&#x3D; 0) &#123;
      return;
    &#125;
    const &#123; _before, _current, _after &#125; &#x3D; split(step, future);
    setHistory(&#123;
      past: [...past, present, ..._before],
      present: _current,
      future: _after,
    &#125;);
  &#125;;
  &#x2F;&#x2F; 向后
  const _backward &#x3D; (step: number &#x3D; -1) &#x3D;&gt; &#123;
    if (past.length &#x3D;&#x3D;&#x3D; 0) &#123;
      return;
    &#125;

    const &#123; _before, _current, _after &#125; &#x3D; split(step, past);
    setHistory(&#123;
      past: _before,
      present: _current,
      future: [..._after, present, ...future],
    &#125;);
  &#125;;
  &#x2F;&#x2F; 可进，可退，可不动
  const go &#x3D; (step: number) &#x3D;&gt; &#123;
    const stepNum &#x3D; isNumber(step) ? step : Number(step);
    if (stepNum &#x3D;&#x3D;&#x3D; 0) &#123;
      return;
    &#125;
    if (stepNum &gt; 0) &#123;
      return _forward(stepNum);
    &#125;
    _backward(stepNum);
  &#125;;

  return &#123;
    value: present, &#x2F;&#x2F; 当前
    backLength: past.length, &#x2F;&#x2F; 可后退长度
    forwardLength: future.length, &#x2F;&#x2F; 可前进长度
    setValue: useMemoizedFn(updateValue), &#x2F;&#x2F; 跳转到哪个记录
    go: useMemoizedFn(go), &#x2F;&#x2F; 回退到哪个
    back: useMemoizedFn(() &#x3D;&gt; &#123;
      &#x2F;&#x2F; 回退一个
      go(-1);
    &#125;),
    forward: useMemoizedFn(() &#x3D;&gt; &#123;
      &#x2F;&#x2F; 前进一个
      go(1);
    &#125;),
    reset: useMemoizedFn(reset), &#x2F;&#x2F; 重置
  &#125;;
&#125;

