&#x2F;&#x2F; 修饰键
const modifierKey &#x3D; &#123;
  ctrl: (event: KeyboardEvent) &#x3D;&gt; event.ctrlKey,
  shift: (event: KeyboardEvent) &#x3D;&gt; event.shiftKey,
  alt: (event: KeyboardEvent) &#x3D;&gt; event.altKey,
  meta: (event: KeyboardEvent) &#x3D;&gt; event.metaKey,
&#125;;

&#x2F;&#x2F; 根据 event 计算激活键数量
function countKeyByEvent(event: KeyboardEvent) &#123;
  const countOfModifier &#x3D; Object.keys(modifierKey).reduce((total, key) &#x3D;&gt; &#123;
    if (modifierKey[key](event)) &#123;
      return total + 1;
    &#125;

    return total;
  &#125;, 0);

  &#x2F;&#x2F; 16 17 18 91 92 是修饰键的 keyCode，如果 keyCode 是修饰键，那么激活数量就是修饰键的数量，如果不是，那么就需要 +1
  return [16, 17, 18, 91, 92].includes(event.keyCode)
    ? countOfModifier
    : countOfModifier + 1;
&#125;

&#x2F;**
 * 判断按键是否激活
 * @param [event: KeyboardEvent]键盘事件
 * @param [keyFilter: any] 当前键
 * @returns Boolean
 *&#x2F;
function genFilterKey(
  event: KeyboardEvent,
  keyFilter: keyType,
  exactMatch: boolean
) &#123;
  &#x2F;&#x2F; 浏览器自动补全 input 的时候，会触发 keyDown、keyUp 事件，但此时 event.key 等为空
  if (!event.key) &#123;
    return false;
  &#125;

  &#x2F;&#x2F; 数字类型直接匹配事件的 keyCode
  if (isNumber(keyFilter)) &#123;
    return event.keyCode &#x3D;&#x3D;&#x3D; keyFilter;
  &#125;

  &#x2F;&#x2F; 字符串依次判断是否有组合键
  const genArr &#x3D; keyFilter.split(&quot;.&quot;);
  let genLen &#x3D; 0;

  for (const key of genArr) &#123;
    &#x2F;&#x2F; 组合键
    const genModifier &#x3D; &#x2F;** 修饰键*&#x2F; modifierKey[key];
    &#x2F;&#x2F; keyCode 别名
    const aliasKeyCode &#x3D;
      &#x2F;**键盘事件 keyCode 别名*&#x2F; aliasKeyCodeMap[key.toLowerCase()];

    if (
      (genModifier &amp;&amp; genModifier(event)) ||
      (aliasKeyCode &amp;&amp; aliasKeyCode &#x3D;&#x3D;&#x3D; event.keyCode)
    ) &#123;
      genLen++;
    &#125;
  &#125;

  &#x2F;**
   * 需要判断触发的键位和监听的键位完全一致，判断方法就是触发的键位里有且等于监听的键位
   * genLen &#x3D;&#x3D;&#x3D; genArr.length 能判断出来触发的键位里有监听的键位
   * countKeyByEvent(event) &#x3D;&#x3D;&#x3D; genArr.length 判断出来触发的键位数量里有且等于监听的键位数量
   * 主要用来防止按组合键其子集也会触发的情况，例如监听 ctrl+a 会触发监听 ctrl 和 a 两个键的事件。
   *&#x2F;
  if (exactMatch) &#123;
    return genLen &#x3D;&#x3D;&#x3D; genArr.length &amp;&amp; countKeyByEvent(event) &#x3D;&#x3D;&#x3D; genArr.length;
  &#125;
  return genLen &#x3D;&#x3D;&#x3D; genArr.length;
&#125;

&#x2F;**
 * 键盘输入预处理方法
 * @param [keyFilter: any] 当前键
 * @returns () &#x3D;&gt; Boolean
 *&#x2F;
function genKeyFormatter(
  keyFilter: KeyFilter,
  exactMatch: boolean
): KeyPredicate &#123;
  if (isFunction(keyFilter)) &#123;
    return keyFilter;
  &#125;
  if (isString(keyFilter) || isNumber(keyFilter)) &#123;
    return (event: KeyboardEvent) &#x3D;&gt; genFilterKey(event, keyFilter, exactMatch);
  &#125;
  if (Array.isArray(keyFilter)) &#123;
    return (event: KeyboardEvent) &#x3D;&gt;
      keyFilter.some((item) &#x3D;&gt; genFilterKey(event, item, exactMatch));
  &#125;
  return keyFilter ? () &#x3D;&gt; true : () &#x3D;&gt; false;
&#125;

const defaultEvents: KeyEvent[] &#x3D; [&quot;keydown&quot;];

function useKeyPress(
  keyFilter: KeyFilter, &#x2F;&#x2F; 支持 keyCode、别名、组合键、数组，自定义函数
  eventHandler: EventHandler, &#x2F;&#x2F; 回调函数
  option?: Options &#x2F;&#x2F; 可选配置项
) &#123;
  const &#123; events &#x3D; defaultEvents, target, exactMatch &#x3D; false &#125; &#x3D; option || &#123;&#125;;
  const eventHandlerRef &#x3D; useLatest(eventHandler); &#x2F;&#x2F; 缓存了下
  const keyFilterRef &#x3D; useLatest(keyFilter); &#x2F;&#x2F; 缓存了下

  useDeepCompareEffectWithTarget(
    () &#x3D;&gt; &#123;
      const el &#x3D; getTargetElement(target, window);
      if (!el) &#123;
        return;
      &#125;

      const callbackHandler &#x3D; (event: KeyboardEvent) &#x3D;&gt; &#123;
        const genGuard: KeyPredicate &#x3D; genKeyFormatter(
          keyFilterRef.current,
          exactMatch
        );
        if (genGuard(event)) &#123;
          &#x2F;&#x2F; 是不是匹配到了，匹配到了执行回调
          return eventHandlerRef.current?.(event);
        &#125;
      &#125;;

      for (const eventName of events) &#123;
        el?.addEventListener?.(eventName, callbackHandler);
      &#125;
      return () &#x3D;&gt; &#123;
        for (const eventName of events) &#123;
          el?.removeEventListener?.(eventName, callbackHandler);
        &#125;
      &#125;;
    &#125;,
    [events],
    target
  );
&#125;

export default useKeyPress;



