useCreation 是 useMemo 或 useRef 的替代品。
因为 useMemo 不能保证被 memo 的值一定不会被重计算，而 useCreation 可以保证这一点。以下为 React 官方文档中的介绍
import type &#123; DependencyList &#125; from &quot;react&quot;;
import &#123; useRef &#125; from &quot;react&quot;;
import depsAreSame from &quot;..&#x2F;utils&#x2F;depsAreSame&quot;;

export default function useCreation&lt;T&gt;(factory: () &#x3D;&gt; T, deps: DependencyList) &#123;
  const &#123; current &#125; &#x3D; useRef(&#123;
    deps,
    obj: undefined as undefined | T,
    initialized: false,
  &#125;);
  &#x2F;&#x2F; 发生变化时候，就会重新计算
  if (current.initialized &#x3D;&#x3D;&#x3D; false || !depsAreSame(current.deps, deps)) &#123;
    current.deps &#x3D; deps;
    current.obj &#x3D; factory(); &#x2F;&#x2F; 工厂
    current.initialized &#x3D; true;
  &#125;
  return current.obj as T;
&#125;

useTrackedEffectimport type &#123; DependencyList &#125; from &quot;react&quot;;
import &#123; useEffect, useRef &#125; from &quot;react&quot;;

type Effect &#x3D; (
  changes?: number[],
  previousDeps?: DependencyList, &#x2F;&#x2F; 之前的依赖
  currentDeps?: DependencyList &#x2F;&#x2F; 之后的依赖
) &#x3D;&gt; void | (() &#x3D;&gt; void);

const diffTwoDeps &#x3D; (deps1?: DependencyList, deps2?: DependencyList) &#x3D;&gt; &#123;
  &#x2F;&#x2F;Let&#39;s do a reference equality check on 2 dependency list.
  &#x2F;&#x2F;If deps1 is defined, we iterate over deps1 and do comparison on each element with equivalent element from deps2
  &#x2F;&#x2F;As this func is used only in this hook, we assume 2 deps always have same length.
  return deps1
    ? &#x2F;&#x2F; deps1（缓存）有， deps2 有 则用Object.is比较
      deps1
        .map((_ele, idx) &#x3D;&gt; (!Object.is(deps1[idx], deps2?.[idx]) ? idx : -1))
        .filter((ele) &#x3D;&gt; ele &gt;&#x3D; 0)
    : &#x2F;&#x2F; deps1（缓存）没有， deps2 有 则把第二个取个遍，否则，返回[]
    deps2
    ? deps2.map((_ele, idx) &#x3D;&gt; idx)
    : [];
&#125;;

const useTrackedEffect &#x3D; (effect: Effect, deps?: DependencyList) &#x3D;&gt; &#123;
  const previousDepsRef &#x3D; useRef&lt;DependencyList&gt;();

  useEffect(() &#x3D;&gt; &#123;
    const changes &#x3D; diffTwoDeps(
      previousDepsRef.current &#x2F;**之前的依赖缓存了 *&#x2F;,
      deps
    );
    const previousDeps &#x3D; previousDepsRef.current;
    previousDepsRef.current &#x3D; deps;
    return effect(changes, previousDeps, deps);
  &#125;, deps);
&#125;;

export default useTrackedEffect;

useVirtualListimport &#123; useEffect, useMemo, useState, useRef &#125; from &quot;react&quot;;
import useEventListener from &quot;..&#x2F;useEventListener&quot;;
import useLatest from &quot;..&#x2F;useLatest&quot;;
import useMemoizedFn from &quot;..&#x2F;useMemoizedFn&quot;;
import useSize from &quot;..&#x2F;useSize&quot;;
import &#123; getTargetElement &#125; from &quot;..&#x2F;utils&#x2F;domTarget&quot;;
import type &#123; BasicTarget &#125; from &quot;..&#x2F;utils&#x2F;domTarget&quot;;
import &#123; isNumber &#125; from &quot;..&#x2F;utils&quot;;

type ItemHeight&lt;T&gt; &#x3D; (index: number, data: T) &#x3D;&gt; number;

export interface Options&lt;T&gt; &#123;
  containerTarget: BasicTarget; &#x2F;&#x2F; 外面容器，支持 DOM 节点或者 Ref 对象
  wrapperTarget: BasicTarget; &#x2F;&#x2F; 内部容器，支持 DOM 节点或者 Ref 对象
  itemHeight: number | ItemHeight&lt;T&gt;; &#x2F;&#x2F; 行高度，静态高度可以直接写入像素值，动态高度可传入函数， 这里的动态是假动态，净骗我，意思是你算好了给 useVirtualList ，就认为你的是动态，并非 “完全自动计算高度”
  overscan?: number; &#x2F;&#x2F; 视区上、下额外展示的 DOM 节点数量
&#125;

const useVirtualList &#x3D; &lt;T &#x3D; any&gt;(list: T[], options: Options&lt;T&gt;) &#x3D;&gt; &#123;
  const &#123; containerTarget, wrapperTarget, itemHeight, overscan &#x3D; 5 &#125; &#x3D; options;

  &#x2F;&#x2F; 缓存了下
  const itemHeightRef &#x3D; useLatest(itemHeight);
  &#x2F;&#x2F; 计算容器宽高
  const size &#x3D; useSize(containerTarget);
  &#x2F;&#x2F; 是不是scrollToFunc 触发的滚动
  const scrollTriggerByScrollToFunc &#x3D; useRef(false);
  &#x2F;&#x2F; 展示的数据
  const [targetList, setTargetList] &#x3D; useState&lt;&#123; index: number; data: T &#125;[]&gt;(
    []
  );
  &#x2F;&#x2F; 可见数量
  const getVisibleCount &#x3D; (containerHeight: number, fromIndex: number) &#x3D;&gt; &#123;
    if (isNumber(itemHeightRef.current)) &#123;
      &#x2F;&#x2F; 容器高度&#x2F;每个元素高度
      return Math.ceil(
        &#x2F;**天花板，向上取整*&#x2F; containerHeight &#x2F; itemHeightRef.current
      );
    &#125;

    let sum &#x3D; 0;
    let endIndex &#x3D; 0;
    for (let i &#x3D; fromIndex; i &lt; list.length; i++) &#123;
      const height &#x3D; itemHeightRef.current(i, list[i]);
      sum +&#x3D; height;
      endIndex &#x3D; i;
      if (sum &gt;&#x3D; containerHeight) &#123;
        break;
      &#125;
    &#125;
    &#x2F;&#x2F; 最后一个可见元素 - 开始元素 &#x3D;  可见元素数量
    return endIndex - fromIndex;
  &#125;;
  &#x2F;&#x2F; 获取间隔数量
  const getOffset &#x3D; (scrollTop: number) &#x3D;&gt; &#123;
    if (isNumber(itemHeightRef.current)) &#123;
      return Math.floor(scrollTop &#x2F; itemHeightRef.current) + 1;
    &#125;
    let sum &#x3D; 0;
    let offset &#x3D; 0;
    for (let i &#x3D; 0; i &lt; list.length; i++) &#123;
      const height &#x3D; itemHeightRef.current(i, list[i]);
      sum +&#x3D; height;
      if (sum &gt;&#x3D; scrollTop) &#123;
        offset &#x3D; i;
        break;
      &#125;
    &#125;
    return offset + 1;
  &#125;;

