js 获取树某个节点场景：编辑树节点，刷新树数据，进行编辑节点回显，某些情况下，树节点被转移他人，当前节点不需要回显
代码&#x2F;**
 *
 * @param &#123;要查询的节点key&#125; targetValue
 * @param &#123;数组形式，树数据&#125; data
 * @returns
 *&#x2F;
const findTreeNodeByKey &#x3D; (
  tree: TreeNode[],
  targetValue: string | number,
  options &#x3D; &#123;
    find_field: &quot;id&quot;,
    children: &quot;children&quot;,
  &#125;
) &#x3D;&gt; &#123;
  let result &#x3D; null;
  const ffindeNodeByKey &#x3D; (
    data: TreeNode[],
    skey: string | number,
    options
  ) &#x3D;&gt; &#123;
    const loop &#x3D; (_data, options) &#x3D;&gt; &#123;
      _data.forEach((item) &#x3D;&gt; &#123;
        if (item[find_field] &#x3D;&#x3D;&#x3D; skey) &#123;
          result &#x3D; item;
        &#125;
        if (item[options.children]) &#123;
          loop(item[options.children], options);
        &#125;
      &#125;);
    &#125;;

    return loop(data, options);
  &#125;;
  ffindeNodeByKey(tree, targetValue, options);
  return result;
&#125;;

