function findNodePaths(tree, targetValue, options) &#123;
  const defaultOptions &#x3D; &#123;
    find_field: &quot;id&quot;, &#x2F;&#x2F; 与 targetValue匹配的字段
    path_field: &quot;id&quot;, &#x2F;&#x2F; 生成路径的字段
