  &#125;;
  console.log(&#39;options&#39;,options)
  options &#x3D; &#123;
    ...defaultOptions,
    ...options,
  &#125;;
  &#x2F;&#x2F; 首先我们先定义个数组，用来保存路径节点id
  let nodePathArray &#x3D; [];

  &#x2F;&#x2F; (tree为目标树，targetId为目标节点id)
  function getNodeRoute(tree, targetValue, options) &#123;
    for (let index &#x3D; 0; index &lt; tree.length; index++) &#123;
      if (tree[index][options.children]) &#123;
        let endRecursiveLoop &#x3D; getNodeRoute(
          tree[index][options.children],
