        &#125; else &#123;
          nodePathArray.push(tree[index]);
        &#125;
        return true;
      &#125;
    &#125;
  &#125;
  &#x2F;&#x2F; 转化成数组
  let _tree &#x3D; [];
  if (Array.isArray(tree)) &#123;
    _tree &#x3D; tree;
  &#125; else &#123;
    _tree &#x3D; [tree];
  &#125;

  getNodeRoute(_tree, targetValue, options);
  console.log(&quot;nodePathArray&quot;, nodePathArray);

  if (options.type &#x3D;&#x3D;&#x3D; &quot;path&quot;) &#123;
    return nodePathArray.reverse().join(options.separator);
  &#125; else &#123;
