    &#x2F;*这里取最后一个为最大高度，[300,50,200],取的是200,😓，文档没说啊 *&#x2F;
    const maxHeight &#x3D; anchors[anchors.length - 1] ?? window.innerHeight;

    const possibles &#x3D; anchors.map((x) &#x3D;&gt; -x); &#x2F;&#x2F; 顶部是负值

    const elementRef &#x3D; useRef&lt;HTMLDivElement&gt;(null);
    const headerRef &#x3D; useRef&lt;HTMLDivElement&gt;(null);
    const contentRef &#x3D; useRef&lt;HTMLDivElement&gt;(null);
    const [pulling, setPulling] &#x3D; useState(false);
    const pullingRef &#x3D; useRef(false);

    const bounds &#x3D; &#123;
      top: possibles[possibles.length - 1], &#x2F;&#x2F; 顶部是负值
      bottom: possibles[0], &#x2F;&#x2F; 底部取0， 0位不一定是最小的😓
    &#125;;

    const [&#123; y &#125;, api] &#x3D; useSpring(() &#x3D;&gt; (&#123;
      &#x2F;&#x2F; useSpring
      y: bounds.bottom,
      config: &#123; tension: 300 &#125;,
    &#125;));

    useDrag(
      (state) &#x3D;&gt; &#123;
        const [, offsetY] &#x3D; state.offset;
        if (state.first) &#123;
          const target &#x3D; state.event.target as Element;
          const header &#x3D; headerRef.current;
          if (header &#x3D;&#x3D;&#x3D; target || header?.contains(target)) &#123;
            pullingRef.current &#x3D; true;
          &#125; else &#123;
            const reachedTop &#x3D; y.goal &lt;&#x3D; bounds.top;
            const content &#x3D; contentRef.current;
            if (!content) return;
            if (reachedTop) &#123;
              if (content.scrollTop &lt;&#x3D; 0 &amp;&amp; state.direction[1] &gt; 0) &#123;
                pullingRef.current &#x3D; true;
              &#125;
            &#125; else &#123;
              pullingRef.current &#x3D; true;
            &#125;
          &#125;
        &#125;
        setPulling(pullingRef.current);
        if (!pullingRef.current) return;
        const &#123; event &#125; &#x3D; state;
        if (event.cancelable) &#123;
          event.preventDefault();
        &#125;
        event.stopPropagation();
        let nextY &#x3D; offsetY;
        if (state.last) &#123;
          pullingRef.current &#x3D; false;
          setPulling(false);
          nextY &#x3D; nearest(possibles, offsetY);
        &#125;
        api.start(&#123;
          y: nextY,
        &#125;);
      &#125;,
      &#123;
        axis: &quot;y&quot;,
        bounds,
        rubberband: true,
        from: () &#x3D;&gt; [0, y.get()],
        pointer: &#123; touch: true &#125;,
        target: elementRef,
        eventOptions: supportsPassive ? &#123; passive: false &#125; : false,
      &#125;
    );

    useImperativeHandle(
      ref,
      () &#x3D;&gt; (&#123;
        setHeight: (
          height: number,
          options?: &#123;
            immediate?: boolean;
          &#125;
        ) &#x3D;&gt; &#123;
          api.start(&#123;
            y: -height,
            immediate: options?.immediate,
          &#125;);
        &#125;,
      &#125;),
      [api]
    );

    return withNativeProps(
      props,
      &lt;animated.div
        ref&#x3D;&#123;elementRef&#125;
        className&#x3D;&quot;adm-floating-panel&quot;
        style&#x3D;&#123;&#123;
          height: maxHeight,
          y,
        &#125;&#125;
      &gt;
        &#x2F;&#x2F; 遮照， 怎么没看到效果呢？？？？
        &lt;div
          className&#x3D;&quot;adm-floating-panel-mask&quot;
          style&#x3D;&#123;&#123;
            display: pulling ? &quot;block&quot; : &quot;none&quot;,
          &#125;&#125;
        &#x2F;&gt;
        &#x2F;&#x2F; bar内容
        &lt;div className&#x3D;&quot;adm-floating-panel-header&quot; ref&#x3D;&#123;headerRef&#125;&gt;
          &lt;div className&#x3D;&quot;adm-floating-panel-bar&quot; &#x2F;&gt;
        &lt;&#x2F;div&gt;
        &lt;div className&#x3D;&quot;adm-floating-panel-content&quot; ref&#x3D;&#123;contentRef&#125;&gt;
          &#123;props.children&#125;
        &lt;&#x2F;div&gt;
      &lt;&#x2F;animated.div&gt;
    );
  &#125;
);

useImperativeHandle









正常情况下 ref 是不能挂在到函数组件上的，因为函数组件没有实例，但是 useImperativeHandle 为我们提供了一个类似实例的东西。它帮助我们通过 useImperativeHandle 的第 2 个参数，所返回的对象的内容挂载到 父组件的 ref.current 上。
forwardRef 会创建一个 React 组件，这个组件能够将其接受的 ref 属性转发到其组件树下的另一个组件中。
