function arr2tree(arr) &#123;
  let hash &#x3D; &#123;&#125;;
  const result &#x3D; [];
  &#x2F;&#x2F; 放到对象中
  for (let item of arr) &#123;
    hash[item.id] &#x3D; item;
  &#125;

  for (let item of arr) &#123;
    const parent &#x3D; hash[item.pid];
    if (parent) &#123;
      if (!parent.children) &#123;
        parent.children &#x3D; [];
      &#125;
      delete item.pid; &#x2F;&#x2F; 比结果多的字段删除
      parent.children.push(item); &#x2F;&#x2F; 之所以能够实现，原因：使用了数组引用
    &#125; else &#123;
      delete item.pid; &#x2F;&#x2F; 比结果多的字段删除
      result.push(item); &#x2F;&#x2F; 只有第一次pid &#x3D; 0的时候，树根节点才走这里
    &#125;
  &#125;
  hash &#x3D; undefined; &#x2F;&#x2F; 这里记得回收
  return result;
&#125;

