      blockedPopTx &#x3D; null;
    &#125; else &#123;
      let nextAction &#x3D; PopAction;
      let [nextIndex, nextLocation] &#x3D; getIndexAndLocation();

      if (blockers.length) &#123;
        if (nextIndex !&#x3D; null) &#123;
          let n &#x3D; index - nextIndex;
          if (n) &#123;
            &#x2F;&#x2F; Revert the POP 还原POP
            blockedPopTx &#x3D; &#123;
              action: nextAction,
