      this.name &#x3D; otherName;
    &#125;
  &#125;

  err() &#123;
    this.name &#x3D; &quot;not ok&quot;;
    &#x2F;&#x2F; Cannot assign to &#39;name&#39; because it is a read-only property.
  &#125;
&#125;
const g &#x3D; new Greeter();
g.name &#x3D; &quot;also not ok&quot;;
