  if (typeof actionCreators !&#x3D;&#x3D; &#39;object&#39; || actionCreators &#x3D;&#x3D;&#x3D; null) &#123;
    throw new Error(
      &#96;bindActionCreators expected an object or a function, but instead received: &#39;$&#123;kindOf(
        actionCreators
