  &#125;
  render() &#123;
    return (
      &lt;table class&#x3D;&quot;table table-striped table-condensed&quot;&gt;
        &lt;thead&gt;
          &lt;tr&gt;
            &lt;th&gt;Country&lt;&#x2F;th&gt;
            &lt;th&gt;Selected country&lt;&#x2F;th&gt;
