              &lt;select value&#x3D;&#123;this.state.country&#125;
                onChange&#x3D;&#123;this.handleChange&#125;
              &gt;
                &lt;option value&#x3D;&quot;&quot;&gt;--Select--&lt;&#x2F;option&gt;
                &lt;option value&#x3D;&quot;India&quot;&gt;India&lt;&#x2F;option&gt;
                &lt;option value&#x3D;&quot;US&quot;&gt;US&lt;&#x2F;option&gt;
                &lt;option value&#x3D;&quot;Dubai&quot;&gt;Dubai&lt;&#x2F;option&gt;
              &lt;&#x2F;select&gt;
